<meta charset="utf-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0, minimum-scale=1, maximum-scale=1.0, shrink-to-fit=no">

<style>
    html {
        box-sizing: border-box;
        -ms-overflow-style: scrollbar;
        height: 100%;
        font-family: sans-serif;
    }

    *,
    *::before,
    *::after {
        box-sizing: inherit;
    }

    body {
        height: 100%;
        margin: 0;
        padding: 1px;
        background: #323232;
        -webkit-font-smoothing: antialiased;
        display: flex;
    }
    
    
    .main {
        margin: auto;
        flex-direction: column;
        display: flex;
        align-items: center;
        opacity: 0;
        animation-name: animationOpacity, animationTranslateY;
        animation-delay: 200ms, 200ms;
        animation-duration: 1500ms, 1500ms;
        animation-fill-mode: forwards, forwards;
    }
    
    header {
        flex-direction: column;
        display: flex;
        align-items: center;
    }
    
    header h1 {
        background-image: url('data:image/png;base64,');
        background-repeat: no-repeat;
        background-size: contain;
        margin: 0;
        padding: 0;
        width: 230px;
        height: 87px;

    }
    
    header span {
        margin-top: 20px;
        color: #c6c4c4;
        font-size: 11px;
        letter-spacing: 3px;
        margin-left: 9px;
    }
    
    .version {
        position: fixed;
        color: #c6c4c4;
        font-size: 13px;
        transform: scaleY(1.8);
        letter-spacing: 0 !important;
        opacity: 0.6;
        bottom: 15px;
        right: 15px;
    }
    
    nav,
    .info {
        margin-top: 35px;
        display: flex;
        flex-direction: column;
        width: 100%;
        box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
        border-radius: 15px;
        animation-name: animationOpacity, animationTranslateY;
        animation-duration: 1500ms, 1500ms;
        animation-fill-mode: forwards, none;
        transition: all 200ms;
        position: relative;
        background: rgba(0, 0, 0, 0.15);
    }
    
    .info {
        animation: none;
        color: #c4c4c4;
        padding: 10px 15px;
        align-items: center;
        position: absolute;
        bottom: -40px;
        font-size: 13px;
        opacity: 0;
        font-weight: bold;
        background: rgba(0, 0, 0, 0.15);
        transition: all 500ms;
    }
    
    .info.active {
        opacity: 1;
    }
    
    nav .button {
        background: none;
        display: flex;
        align-items: center;
        justify-content: center;
        border: none;
        color: #bfbfbf;
        font-family: sans-serif;
        cursor: pointer;
        transition: all 200ms;
        font-size: 13px;
        font-weight: bold;
        opacity: 0;
        animation-name: animationOpacity, animationTranslateY;
        animation-duration: 1500ms, 1500ms;
        animation-fill-mode: forwards, none;
        margin: 0 1px;
        transform: translateY(0);
        flex-grow: 1;
        padding: 10px 25px;
    }
    
    nav .button:hover {
        transform: translateY(-2px);
    }
    
    nav .button:nth-child(2) {
        animation-delay: 600ms;
    }
    nav .button:nth-child(3) {
        animation-delay: 800ms;
    }
    
    nav .button .icon {
        width: 22px;
        height: 22px;
        background-size: contain;
        opacity: 0.8;
        margin-right: 5px;
    }
    
    nav .button .icon.docs {
        background-image: url("data:image/svg+xml;base64,");
    }
    
    nav .button .icon.github {
        background-image: url("data:image/svg+xml;base64,");
    }
    
    @media (min-width: 425px) {
        nav {
            flex-direction: row;
        }
    }
    
    @media (min-width: 768px) {
        header h1 {
            width: 350px;
            height: 132px;
        }

        header span {
            letter-spacing: 9px;
        }
    }
    
    @keyframes animationTranslateY {
        from {
            transform: translateY(-20px);
        }
        
        to {
            transform: translateY(0);
        }
    }
    
    @keyframes animationOpacity {
        from {
            opacity: 0;
        }

        to {
            opacity: 1;
        }
    }
</style>

<script>
    function load(){
        var buttons = document.getElementsByClassName("button");
        var info = document.getElementsByClassName("info")[0];

        for(var i=0; i<buttons.length; i++){
            var button = buttons[i];

            button.addEventListener("mouseenter", function(event){
                var message = this.getAttribute('info');

                if(typeof message != 'undefined' && message != null){
                    info.classList.add('active');
                    info.innerHTML = message;
                }
            });

            button.addEventListener("mouseleave", function(event){
                info.classList.remove('active');
            });
        }
    }
</script>

<div class="main">
    <header>
        <h1></h1>
        <span>UM MICROFRAMEWORK PHP7</span>
    </header>

    <nav>
        <div class="info"></div>
        
        <a class="button" info="Em breve...">
            <div class="icon docs"></div>
            <span>DOCUMENTAÇÃO</span>
        </a>
        
        <a class="button" href="https://github.com/andregalastri/galastri-framework" target="_blank">
            <div class="icon github"></div>
            <span>GITHUB</span>
        </a>
    </nav>
</div>

<div class='version'>0.5.0 alpha</div>

<script>load();</script>
